mapper.select:
SELECT p.p_id, p.p_name, p.p_code, p.p_category, p.p_unit, p.p_price, p.stock,
       p.s_id, s.s_name, p.p_description, p.p_status
FROM product p
LEFT JOIN supplier s ON p.s_id = s.s_id


        AND p.p_name LIKE CONCAT('%', #{pName}, '%')


        AND p.p_code LIKE CONCAT('%', #{pCode}, '%')


        AND p.p_category = #{pCategory}


        AND p.s_id = #{sId}


        AND p.p_status = #{pStatus}


ORDER BY p.p_id DESC

mapper.update:
UPDATE product
p_name = #{pName}, p_code = #{pCode}, p_category = #{pCategory}, p_unit = #{pUnit}, p_price = #{pPrice}, stock = #{stock}, s_id = #{sId}, p_description = #{pDescription}, p_status = #{pStatus},
WHERE p_id = #{pId}
